package pack1;

public class Q9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10, sum = 0;  
		
		for(int i = 1; i <= num; ++i)  
		{  
		  
		sum = sum + i;  
		}  
		  
		System.out.println("Sum of First 10 Natural Numbers is = " + sum);  
	}

}